    "content": "import os
import torch

class Trainer:
    def __init__(self, model, optimizer, train_loader, val_loader, epochs, checkpoint_dir='checkpoints', checkpoint_freq=10):
        self.model = model
        self.optimizer = optimizer
        self.train_loader = train_loader
        self.val_loader = val_loader
        self.epochs = epochs
        self.checkpoint_dir = checkpoint_dir
        self.checkpoint_freq = checkpoint_freq

        if not os.path.exists(self.checkpoint_dir):
            os.makedirs(self.checkpoint_dir)

    def train(self):
        for epoch in range(self.epochs):
            self.train_epoch(epoch)
            self.validate_epoch(epoch)

            if (epoch + 1) % self.checkpoint_freq == 0:
                self.save_checkpoint(epoch)

    def train_epoch(self, epoch):
        self.model.train()
        for batch_idx, (data, target) in enumerate(self.train_loader):
            self.optimizer.zero_grad()
            output = self.model(data)
            loss = torch.nn.functional.cross_entropy(output, target)
            loss.backward()
            self.optimizer.step()
            if batch_idx % 100 == 0:
                print(f'Train Epoch: {epoch} [{batch_idx * len(data)}/{len(self.train_loader.dataset)} ({100. * batch_idx / len(self.train_loader):.0f}%)]\tLoss: {loss.item():.6f}')

    def validate_epoch(self, epoch):
        self.model.eval()
        val_loss = 0
        correct = 0
        with torch.no_grad():
            for data, target in self.val_loader:
                output = self.model(data)
                val_loss += torch.nn.functional.cross_entropy(output, target, reduction='sum').item()
                pred = output.argmax(dim=1, keepdim=True)
                correct += pred.eq(target.view_as(pred)).sum().item()

        val_loss /= len(self.val_loader.dataset)
        print(f'\nValidation set: Average loss: {val_loss:.4f}, Accuracy: {correct}/{len(self.val_loader.dataset)} ({100. * correct / len(self.val_loader.dataset):.0f}%)\n')

    def save_checkpoint(self, epoch):
        checkpoint_path = os.path.join(self.checkpoint_dir, f'checkpoint_epoch_{epoch+1}.pth')
        torch.save({
            'epoch': epoch + 1,
            'model_state_dict': self.model.state_dict(),
            'optimizer_state_dict': self.optimizer.state_dict(),
        }, checkpoint_path)
        print(f'Checkpoint saved to {checkpoint_path}')
