import traceback

# Configure logging
logging.basicConfig(level=logging.INFO, format='%(asctime)s - %(levelname)s - %(message)s')

class EvolutionSystem:
    def __init__(self, code_generator, code_validator):
        self.code_generator = code_generator
        self.code_validator = code_validator

    def evolve(self, task_description):
        logging.info(f'Starting evolution for task: {task_description}')
        try:
            generated_code = self.code_generator.generate_code(task_description)
            logging.info(f'Code generated successfully.')
        except Exception as e:
            logging.error(f'Code generation failed: {e}')
            logging.error(traceback.format_exc())
            return None

        try:
            is_valid, errors = self.code_validator.validate_code(generated_code)
            logging.info(f'Code validation result: {is_valid}')
            if not is_valid:
                logging.warning(f'Validation errors: {errors}')
        except Exception as e:
            logging.error(f'Code validation failed: {e}')
            logging.error(traceback.format_exc())
            return None

        if is_valid:
            logging.info('Evolution successful.')
            return generated_code
        else:
            logging.info('Evolution failed due to validation errors.')
            return None
