    "content": "class Trainer:
    def __init__(self, model, optimizer, criterion, device, train_loader, val_loader=None):
        self.model = model
        self.optimizer = optimizer
        self.criterion = criterion
        self.device = device
        self.train_loader = train_loader
        self.val_loader = val_loader

    def train_step(self, data, target):
        self.model.train()
        self.optimizer.zero_grad()
        output = self.model(data.to(self.device))
        loss = self.criterion(output, target.to(self.device))
        loss.backward()
        self.optimizer.step()
        return loss.item()

    def val_step(self):  # new validation function
        self.model.eval()
        total_loss = 0
        with torch.no_grad():
            for data, target in self.val_loader:
                output = self.model(data.to(self.device))
                loss = self.criterion(output, target.to(self.device))
                total_loss += loss.item()
        return total_loss / len(self.val_loader) if self.val_loader else None # return mean loss


    def train_epoch(self):
        total_loss = 0
        for data, target in self.train_loader:
            loss = self.train_step(data, target)
            total_loss += loss
        return total_loss / len(self.train_loader)

    def train(self, epochs):
        for epoch in range(epochs):
            train_loss = self.train_epoch()
            val_loss = self.val_step() if self.val_loader else None # add val loss
            print(f'Epoch {epoch+1}, Train Loss: {train_loss:.4f}', end='')
            if val_loss:
                print(f', Validation Loss: {val_loss:.4f}')
            else:
                print('')

