    "content": "import torch
import torch.nn as nn
import torch.optim as optim
from torch.utils.data import DataLoader

class Trainer:
    def __init__(self, model: nn.Module, train_dataloader: DataLoader, optimizer: optim.Optimizer, device: torch.device, lr_scheduler=None, grad_accumulation_steps=1, use_amp=False):
        self.model = model
        self.train_dataloader = train_dataloader
        self.optimizer = optimizer
        self.device = device
        self.lr_scheduler = lr_scheduler
        self.grad_accumulation_steps = grad_accumulation_steps
        self.use_amp = use_amp
        if self.use_amp:
            self.scaler = torch.cuda.amp.GradScaler()

    def train_one_epoch(self):
        self.model.train()
        total_loss = 0.0
        self.optimizer.zero_grad()
        for i, batch in enumerate(self.train_dataloader):
            inputs, labels = batch
            inputs = inputs.to(self.device)
            labels = labels.to(self.device)

            with torch.cuda.amp.autocast(enabled=self.use_amp):
                outputs = self.model(inputs)
                loss = nn.CrossEntropyLoss()(outputs, labels)
                loss = loss / self.grad_accumulation_steps

            if self.use_amp:
                self.scaler.scale(loss).backward()
            else:
                loss.backward()

            if (i + 1) % self.grad_accumulation_steps == 0:
                if self.use_amp:
                    self.scaler.step(self.optimizer)
                    self.scaler.update()
                else:
                    self.optimizer.step()
                self.optimizer.zero_grad()
                if self.lr_scheduler:
                    self.lr_scheduler.step()

            total_loss += loss.item() * self.grad_accumulation_steps

        return total_loss / len(self.train_dataloader)

    def train(self, epochs: int):
        for epoch in range(epochs):
            avg_loss = self.train_one_epoch()
            print(f"Epoch {epoch+1}/{epochs}, Loss: {avg_loss:.4f}")
