        # Add the handler to the logger
        self.logger.addHandler(fh)

    def evolve(self):
        try:
            # Simulate an evolution step
            if self.state < 0:
                raise ValueError("State cannot be negative")
            self.state += 1
            self.logger.info(f"Evolved to state: {self.state}")
        except ValueError as e:
            self.logger.error(f"ValueError during evolution: {e}")
            self.logger.error(traceback.format_exc())
            self.handle_evolution_error(e)
        except Exception as e:
            self.logger.error(f"Unexpected error during evolution: {e}")
            self.logger.error(traceback.format_exc())
            self.handle_evolution_error(e)

    def handle_evolution_error(self, error):
