    def get_project_context(self, important_files=None):
        context = {}
        if important_files is None:
            important_files = []

        for file_path in important_files:
            content = self.read_file_content(file_path)
            if content:
                context[file_path] = content
            else:
                print(f"Failed to read content from {file_path}")

        return context


    def extract_function_signatures(self, file_content):
        signatures = []
        try:
            module = ast.parse(file_content)
            for node in ast.walk(module):
                if isinstance(node, ast.FunctionDef):
                    args = ', '.join([arg.arg for arg in node.args.args])
                    signatures.append(f"def {node.name}({args}):")
        except SyntaxError as e:
            print(f"SyntaxError: {e}")
        return signatures"
