    "content": "import os
import glob

class ProjectContext:
    def __init__(self, project_root):
        self.project_root = project_root

    def get_relevant_files(self, num_files=5):
        """Returns a list of relevant file paths within the project, limited by num_files."""
        all_files = []
        for ext in ['*.py', '*.txt', '*.md']:
            all_files.extend(glob.glob(os.path.join(self.project_root, '**', ext), recursive=True))

        # Exclude files in .venv and node_modules directories
        relevant_files = [f for f in all_files if '.venv' not in f and 'node_modules' not in f]

        # Sort files by modification time (newest first)
        relevant_files.sort(key=os.path.getmtime, reverse=True)

        return relevant_files[:num_files]

    def get_file_content(self, file_path):
        """Returns the content of a file, handling potential errors."""
        try:
            with open(file_path, 'r', encoding='utf-8') as f:
                return f.read()
        except Exception as e:
            print(f"Error reading file {file_path}: {e}")
            return ""  # Return empty string in case of error

    def get_project_context(self, num_files=5):
        """Gathers project context by reading a limited number of relevant files."""
        relevant_files = self.get_relevant_files(num_files)
        context = {}
        for file_path in relevant_files:
            context[file_path] = self.get_file_content(file_path)
        return context


if __name__ == '__main__':
    # Example Usage:
    project_root = os.getcwd()  # Assuming the script is run from the project root
    project_context = ProjectContext(project_root)

    # Get context from top 3 most recently modified files
    context = project_context.get_project_context(num_files=3)

    for file_path, content in context.items():
        print(f"File: {file_path}\nContent:\n{content[:200]}...\n") # Print first 200 chars
