    "content": "import logging

logger = logging.getLogger(__name__)


def safe_operation(func, *args, **kwargs):
    """Wraps a function call with try-except block for safe execution.

    Logs any exceptions that occur and returns None, preventing crashes.
    """
    try:
        return func(*args, **kwargs)
    except Exception as e:
        logger.exception(f"Exception in {func.__name__}: {e}")
        return None


def handle_error(message, exception=None):
    """Logs an error message along with an optional exception.

    Simplifies error logging and provides context.
    """
    if exception:
        logger.error(f"{message}: {exception}", exc_info=True)
    else:
        logger.error(message)


class CustomError(Exception):
    """Base class for custom exceptions in the project."""
    pass


class ValidationError(CustomError):
    """Exception raised for validation errors."""
    pass


class ConfigurationError(CustomError):
    """Exception raised for configuration errors."""
    pass
