    "content": "import pandas as pd

class DataProcessor:
    def __init__(self, file_path):
        self.file_path = file_path
        self.data = None

    def load_data(self):
        # Use chunking to read the CSV in smaller parts
        self.data = pd.concat(pd.read_csv(self.file_path, chunksize=10000))
        return self.data

    def calculate_mean(self, column_name):
        # Calculate the mean on the loaded data
        if self.data is None:
            self.load_data()
        return self.data[column_name].mean()

    def filter_data(self, column_name, threshold):
        # Filter the data based on a threshold
        if self.data is None:
            self.load_data()
        return self.data[self.data[column_name] > threshold]

    def apply_transformation(self, column_name, func):
        # Apply a transformation function to a column
        if self.data is None:
            self.load_data()
        self.data[column_name] = self.data[column_name].apply(func)
        return self.data

    def get_dataframe(self):
        # Return the dataframe
        if self.data is None:
            self.load_data()
        return self.data
