    "content": "import google.generativeai as genai
import json

class EvolutionSystem:
    def __init__(self, api_key, model_name='gemini-1.0-pro'):
        genai.configure(api_key=api_key)
        self.model = genai.GenerativeModel(model_name)

    def generate_evolution(self, current_code, task_description, max_output_tokens=2048):
        prompt = f"""You are an AI code evolution expert. Given the current code and task description, generate improved code.
        Current Code:
        {current_code}
        Task Description:
        {task_description}
        Respond ONLY with the improved code, and ensure it's valid and complete.
        Do not include any explanations or surrounding text.
        """
        try:
            response = self.model.generate_content(prompt, generation_config=genai.types.GenerationConfig(max_output_tokens=max_output_tokens))
            return response.text
        except Exception as e:
            print(f"Error during code generation: {e}")
            return None

    def generate_evolution_json(self, current_code, task_description, max_output_tokens=2048):
        prompt = f"""You are an AI code evolution expert. Given the current code and task description, generate improved code and return it as a JSON object.
        Current Code:
        {current_code}
        Task Description:
        {task_description}
        Respond ONLY with a JSON object containing the 'code' key and the improved code as its value. Ensure the code is valid and complete.
        Example: {{"code": "...improved code here..."}}
        Do not include any explanations or surrounding text.
        """
        try:
            response = self.model.generate_content(prompt, generation_config=genai.types.GenerationConfig(max_output_tokens=max_output_tokens))
            try:
                json_response = json.loads(response.text)
                return json_response['code']
            except (json.JSONDecodeError, KeyError) as e:
                print(f"Error decoding JSON response: {e}")
                return None
        except Exception as e:
            print(f"Error during code generation: {e}")
            return None"
