    def evolve(self, selection_strategy, crossover_strategy, mutation_strategy, generations):
        for generation in range(generations):
            # Selection
            selected_parents = selection_strategy.select(self.population)

            # Crossover
            offspring = []
            for i in range(0, len(selected_parents), 2):
                if i + 1 < len(selected_parents):
                    parent1 = selected_parents[i]
                    parent2 = selected_parents[i + 1]
                    child1, child2 = crossover_strategy.crossover(parent1, parent2)
                    offspring.extend([child1, child2])
                else:
                    offspring.append(selected_parents[i]) # In case of odd number of parents

            # Mutation
            mutated_offspring = [mutation_strategy.mutate(individual) for individual in offspring]

            # Replace population.  Crucially, clear the old population *before* adding the new one
            # to avoid a large memory spike.
            self.population.clear()
            gc.collect()
            self.population.extend(mutated_offspring)
            gc.collect() # Trigger garbage collection after each generation

    def get_best_individual(self, fitness_function):
