    "content": "import abc
import torch
from torch.optim import Optimizer
from torch.utils.data import DataLoader

class BaseTrainer(abc.ABC):
    def __init__(self, model: torch.nn.Module, train_dataloader: DataLoader, val_dataloader: DataLoader | None = None):
        self.model = model
        self.train_dataloader = train_dataloader
        self.val_dataloader = val_dataloader
        self.optimizer = None
        self.lr_scheduler = None

    @abc.abstractmethod
    def train_step(self, batch: torch.Tensor) -> torch.Tensor:
        pass

    @abc.abstractmethod
    def validation_step(self, batch: torch.Tensor) -> float:
        pass

    @abc.abstractmethod
    def configure_optimizers(self) -> tuple[Optimizer, _LRScheduler]:
        pass

    def train(self, epochs: int):
        self.optimizer, self.lr_scheduler = self.configure_optimizers()
        for epoch in range(epochs):
            self.model.train()
            train_loss = 0.0
            for batch in self.train_dataloader:
                loss = self.train_step(batch)
                train_loss += loss.item()
                self.optimizer.zero_grad()
                loss.backward()
                self.optimizer.step()
                if self.lr_scheduler:
                    self.lr_scheduler.step()

            print(f"Epoch {epoch+1}, Train Loss: {train_loss / len(self.train_dataloader)}")

            if self.val_dataloader:
                self.model.eval()
                val_loss = 0.0
                with torch.no_grad():
                    for batch in self.val_dataloader:
                        loss = self.validation_step(batch)
                        val_loss += loss

                print(f"Epoch {epoch+1}, Val Loss: {val_loss / len(self.val_dataloader)}")

