import pandas as pd


def setup_logging(level=logging.INFO):
    """Sets up basic logging configuration."""
    logging.basicConfig(
        level=level,
        format='%(asctime)s - %(levelname)s - %(message)s'
    )


def load_data(filepath):
    """Loads data from a CSV file into a Pandas DataFrame."""
    try:
        df = pd.read_csv(filepath)
        logging.info(f"Data loaded successfully from {filepath}")
        return df
    except FileNotFoundError:
        logging.error(f"File not found: {filepath}")
        return None
    except Exception as e:
        logging.error(f"Error loading data from {filepath}: {e}")
        return None


def preprocess_data(df):
    """Performs basic data preprocessing (example: dropping duplicates)."""
    if df is None:
        logging.warning("No data to preprocess.")
        return None

    initial_shape = df.shape
    df = df.drop_duplicates()
    new_shape = df.shape

    if initial_shape != new_shape:
        logging.info(f"Dropped {initial_shape[0] - new_shape[0]} duplicate rows.")
    else:
        logging.info("No duplicate rows found.")

    return df


if __name__ == '__main__':
    # Example Usage (This won't run during automated testing)
    setup_logging()
