    "content": "import torch
import torch.nn as nn
import torch.optim as optim
from torch.optim.lr_scheduler import StepLR
import os

class Trainer:
    def __init__(self, model, train_loader, val_loader, device, 
                 loss_fn=None, optimizer=None, scheduler=None,
                 learning_rate=1e-3, optimizer_type='Adam',
                 scheduler_step_size=30, scheduler_gamma=0.1, save_dir='checkpoints'):
        self.model = model.to(device)
        self.train_loader = train_loader
        self.val_loader = val_loader
        self.device = device
        self.save_dir = save_dir
        os.makedirs(self.save_dir, exist_ok=True)

        # Use provided loss function or default to CrossEntropyLoss
        self.loss_fn = loss_fn or nn.CrossEntropyLoss()

        # Use provided optimizer or create a default one
        if optimizer is None:
            if optimizer_type == 'Adam':
                self.optimizer = optim.Adam(model.parameters(), lr=learning_rate)
            elif optimizer_type == 'SGD':
                self.optimizer = optim.SGD(model.parameters(), lr=learning_rate)
            else:
                raise ValueError(f"Unsupported optimizer type: {optimizer_type}")
        else:
            self.optimizer = optimizer

        # Use provided scheduler or create a default one
        if scheduler is None:
            self.scheduler = StepLR(self.optimizer, step_size=scheduler_step_size, gamma=scheduler_gamma)
        else:
            self.scheduler = scheduler


    def train_epoch(self):
        self.model.train()
        running_loss = 0.0
        for inputs, labels in self.train_loader:
            inputs, labels = inputs.to(self.device), labels.to(self.device)
            self.optimizer.zero_grad()
            outputs = self.model(inputs)
            loss = self.loss_fn(outputs, labels)
            loss.backward()
            self.optimizer.step()
            running_loss += loss.item()
        return running_loss / len(self.train_loader)

    def val_epoch(self):
        self.model.eval()
        running_loss = 0.0
        with torch.no_grad():
            for inputs, labels in self.val_loader:
                inputs, labels = inputs.to(self.device), labels.to(self.device)
                outputs = self.model(inputs)
                loss = self.loss_fn(outputs, labels)
                running_loss += loss.item()
        return running_loss / len(self.val_loader)

    def train(self, epochs):
        for epoch in range(epochs):
            train_loss = self.train_epoch()
            val_loss = self.val_epoch()
            self.scheduler.step()
            print(f'Epoch {epoch+1}, Train Loss: {train_loss:.4f}, Val Loss: {val_loss:.4f}')

    def save_model(self, filename='checkpoint.pth'):
        filepath = os.path.join(self.save_dir, filename)
        torch.save({
            'model_state_dict': self.model.state_dict(),
            'optimizer_state_dict': self.optimizer.state_dict(),
            'scheduler_state_dict': self.scheduler.state_dict(),
        }, filepath)
        print(f'Model saved to {filepath}')

    def load_model(self, filename='checkpoint.pth'):
        filepath = os.path.join(self.save_dir, filename)
        if os.path.exists(filepath):
            checkpoint = torch.load(filepath, map_location=self.device)
            self.model.load_state_dict(checkpoint['model_state_dict'])
            self.optimizer.load_state_dict(checkpoint['optimizer_state_dict'])
            self.scheduler.load_state_dict(checkpoint['scheduler_state_dict'])
            print(f'Model loaded from {filepath}')
        else:
            print(f'Checkpoint file not found: {filepath}')
