    "content": "import psutil
import time
import threading
import json

class MemoryMonitor:
    def __init__(self, interval=1, log_file='memory_log.json'):
        self.interval = interval
        self.log_file = log_file
        self.stop_flag = threading.Event()
        self.data = []
        self.thread = None

    def start(self):
        self.stop_flag.clear()
        self.thread = threading.Thread(target=self._monitor)
        self.thread.daemon = True
        self.thread.start()

    def stop(self):
        self.stop_flag.set()
        if self.thread:
            self.thread.join()
        self.save_to_file()

    def _monitor(self):
        while not self.stop_flag.is_set():
            memory_usage = psutil.virtual_memory().percent
            timestamp = time.time()
            self.data.append({'timestamp': timestamp, 'memory_usage': memory_usage})
            time.sleep(self.interval)

    def save_to_file(self):
        try:
            with open(self.log_file, 'w') as f:
                json.dump(self.data, f, indent=4)
        except Exception as e:
            print(f"Error saving memory log: {e}")

    def get_data(self):
        return self.data

if __name__ == '__main__':
    monitor = MemoryMonitor(interval=0.5)
    monitor.start()
    time.sleep(5)
    monitor.stop()
    print("Memory monitoring stopped. Log saved to memory_log.json")"
