import ast


def get_project_context(project_dir, num_lines=100):
    """Extracts project context by prioritizing class and function definitions.

    Args:
        project_dir (str): The project directory.
        num_lines (int): The maximum number of lines to read from each file.

    Returns:
        str: A string containing the project context.
    """
    context = ""
    for root, _, files in os.walk(project_dir):
        for file in files:
            if file.endswith(".py"):
                file_path = os.path.join(root, file)
                try:
                    with open(file_path, "r") as f:
                        tree = ast.parse(f.read())

                    definitions = []
                    for node in ast.walk(tree):
                        if isinstance(node, (ast.ClassDef, ast.FunctionDef)):
                            definition_string = ast.unparse(node)  # Use ast.unparse to get the code
                            definitions.append(definition_string)

                    if definitions:
                        context += f"\n--- {file_path} ---\n"
                        context += "\n".join(definitions) + "\n"
                    else:
                        # Fallback to reading the first num_lines if no definitions are found
                        with open(file_path, "r") as f:
                            content = "".join(f.readlines()[:num_lines])
                            context += f"\n--- {file_path} ---\n{content}\n"


                except Exception as e:
                    context += f"\n--- {file_path} ---\nError reading file: {e}\n"
    return context
