class EvolutionSystem:
    def __init__(self, initial_state=None, state_file='evolution_state.json'):
        self.state = initial_state or {}
        self.state_file = state_file
        self.load_state()

    def load_state(self):
        try:
            with open(self.state_file, 'r') as f:
                self.state = json.load(f)
        except FileNotFoundError:
            print(f"State file not found: {self.state_file}. Starting with an empty state.")
            self.state = {}
        except json.JSONDecodeError:
            print(f"Error decoding state file: {self.state_file}. Starting with an empty state.")
            self.state = {}
        except Exception as e:
            print(f"Error loading state from {self.state_file}: {e}. Starting with an empty state.")
            self.state = {}


    def save_state(self):
        try:
            with open(self.state_file, 'w') as f:
                json.dump(self.state, f, indent=4)
        except Exception as e:
            print(f"Error saving state to {self.state_file}: {e}")

    def update_state(self, key, value):
        self.state[key] = value
        self.save_state()

    def get_state(self, key):
        return self.state.get(key)

    def reset_state(self):
        self.state = {}
        self.save_state()
