    "content": "import torch
import torch.nn as nn
import torch.optim as optim
from torch.utils.data import DataLoader
import json

class Trainer:
    def __init__(self, model, train_dataset, val_dataset, config_path):
        self.model = model
        self.train_dataset = train_dataset
        self.val_dataset = val_dataset
        self.config = self.load_config(config_path)
        self.device = torch.device("cuda" if torch.cuda.is_available() else "cpu")
        self.model.to(self.device)
        self.train_loader = DataLoader(train_dataset, batch_size=self.config['batch_size'], shuffle=True)
        self.val_loader = DataLoader(val_dataset, batch_size=self.config['batch_size'], shuffle=False)

        self.optimizer = self.get_optimizer()
        self.criterion = self.get_loss_function()

    def load_config(self, config_path):
        with open(config_path, 'r') as f:
            return json.load(f)

    def get_optimizer(self):
        optimizer_config = self.config['optimizer']
        optimizer_name = optimizer_config['name']
        learning_rate = optimizer_config['learning_rate']
        weight_decay = optimizer_config.get('weight_decay', 0) # Optional weight decay

        if optimizer_name == 'Adam':
            return optim.Adam(self.model.parameters(), lr=learning_rate, weight_decay=weight_decay)
        elif optimizer_name == 'SGD':
            momentum = optimizer_config.get('momentum', 0) # Optional momentum
            return optim.SGD(self.model.parameters(), lr=learning_rate, weight_decay=weight_decay, momentum=momentum)
        else:
            raise ValueError(f"Unsupported optimizer: {optimizer_name}")

    def get_loss_function(self):
        loss_name = self.config['loss_function']['name']
        if loss_name == 'CrossEntropyLoss':
            return nn.CrossEntropyLoss()
        elif loss_name == 'MSELoss':
            return nn.MSELoss()
        else:
            raise ValueError(f"Unsupported loss function: {loss_name}")

    def train_epoch(self):
        self.model.train()
        total_loss = 0
        for batch_idx, (data, target) in enumerate(self.train_loader):
            data, target = data.to(self.device), target.to(self.device)
            self.optimizer.zero_grad()
            output = self.model(data)
            loss = self.criterion(output, target)
            loss.backward()
            self.optimizer.step()
            total_loss += loss.item()
        return total_loss / len(self.train_loader)

    def validate_epoch(self):
        self.model.eval()
        total_loss = 0
        with torch.no_grad():
            for batch_idx, (data, target) in enumerate(self.val_loader):
                data, target = data.to(self.device), target.to(self.device)
                output = self.model(data)
                loss = self.criterion(output, target)
                total_loss += loss.item()
        return total_loss / len(self.val_loader)

    def train(self, num_epochs):
        for epoch in range(num_epochs):
            train_loss = self.train_epoch()
            val_loss = self.validate_epoch()
            print(f"Epoch {epoch+1}/{num_epochs}, Train Loss: {train_loss:.4f}, Val Loss: {val_loss:.4f}")

    def set_optimizer(self, optimizer):
        self.optimizer = optimizer

    def set_loss_function(self, criterion):
        self.criterion = criterion"
