import os

class EvolutionSystem:
    def __init__(self, code_generator, code_evaluator, code_improver, logger):
        self.code_generator = code_generator
        self.code_evaluator = code_evaluator
        self.code_improver = code_improver
        self.logger = logger

    def evolve(self, task_description, initial_code=None, max_iterations=5):
        current_code = initial_code if initial_code else self.code_generator.generate_code(task_description)
        history = []

        for i in range(max_iterations):
            self.logger.log(f"Iteration {{i + 1}}:")
            self.logger.log(f"Current Code:\n{{current_code}}")

            # Evaluate Code
            evaluation_result = self.code_evaluator.evaluate_code(current_code, task_description)
            self.logger.log(f"Evaluation Result: {{evaluation_result}}")
            history.append({"code": current_code, "evaluation": evaluation_result})

            # Improve Code
            try:
                improved_code = self.code_improver.improve_code(current_code, evaluation_result, task_description)
                self.logger.log(f"Improved Code:\n{{improved_code}}")
                current_code = improved_code
            except Exception as e:
                self.logger.log(f"Error during code improvement: {{e}}", level='error')
                self.logger.log(f"Iteration {{i + 1}} failed to improve code. Halting evolution.", level='error')
                break # Stop evolving if improvement fails.

        return history

if __name__ == '__main__':
