    "content": "import json

class DataLoader:
    def __init__(self, file_path):
        self.file_path = file_path

    def stream_load_data(self):
        """Loads data in a streaming fashion to reduce memory usage."""
        try:
            with open(self.file_path, 'r') as f:
                for line in f:
                    # Assuming each line is a valid JSON object
                    try:
                        yield json.loads(line)
                    except json.JSONDecodeError as e:
                        print(f"Error decoding JSON in line: {line}. Error: {e}")
                        continue # Skip to the next line, or handle the error as needed

        except FileNotFoundError:
            print(f"File not found: {self.file_path}")
            yield None
        except Exception as e:
            print(f"An error occurred: {e}")
            yield None

    def load_all_data(self):
        """Loads all data at once (for comparison or small datasets)."""
        try:
            with open(self.file_path, 'r') as f:
                data = json.load(f)
                return data
        except FileNotFoundError:
            print(f"File not found: {self.file_path}")
            return None
        except json.JSONDecodeError:
            print(f"Error decoding JSON from file: {self.file_path}")
            return None
        except Exception as e:
            print(f"An error occurred: {e}")
            return None"
