    "content": "import os
import logging

from h2q_project.llm_api_client import LLMApiClient

logger = logging.getLogger(__name__)

class EvolutionSystem:
    def __init__(self, initial_code):
        self.current_code = initial_code
        # Initialize LLM API client with API key from environment variable
        api_key = os.environ.get("GEMINI_API_KEY")
        if not api_key:
            logger.warning("GEMINI_API_KEY not found in environment variables. Some functionalities may be limited.")
            self.llm_client = None
        else:
            self.llm_client = LLMApiClient(api_key)

    def evolve(self, objective):
        if self.llm_client is None:
            logger.warning("LLM client is not initialized. Skipping evolution step.")
            return self.current_code

        prompt = f"Improve the following code to achieve the objective: {objective}\n\n{self.current_code}"
        new_code = self.llm_client.generate_code(prompt)
        self.current_code = new_code
        return self.current_code
