    "content": "class Trainer:
    def __init__(self, model, optimizer, loss_fn, train_dataloader, val_dataloader=None):
        self.model = model
        self.optimizer = optimizer
        self.loss_fn = loss_fn
        self.train_dataloader = train_dataloader
        self.val_dataloader = val_dataloader

    def train_step(self, data, target):
        self.model.train()
        self.optimizer.zero_grad()
        output = self.model(data)
        loss = self.loss_fn(output, target)
        loss.backward()
        self.optimizer.step()
        return loss.item()

    def val_step(self, data, target):
        self.model.eval()
        output = self.model(data)
        loss = self.loss_fn(output, target)
        return loss.item()

    def train(self, epochs):
        for epoch in range(epochs):
            total_loss = 0
            for data, target in self.train_dataloader:
                loss = self.train_step(data, target)
                total_loss += loss
            print(f'Epoch {epoch+1}, Loss: {total_loss/len(self.train_dataloader)}')

            if self.val_dataloader:
                total_val_loss = 0
                for data, target in self.val_dataloader:
                    loss = self.val_step(data, target)
                    total_val_loss += loss
                print(f'Epoch {epoch+1}, Val Loss: {total_val_loss/len(self.val_dataloader)}')
