def get_project_context(project_path, max_files=10):
    """Extracts project context by analyzing Python files.

    Instead of simply reading the first N lines, it extracts
    class and function signatures using AST analysis for
    a more informative summary.
    """
    context = []
    file_count = 0
    for root, _, files in os.walk(project_path):
        for file in files:
            if file.endswith(".py"):
                file_path = os.path.join(root, file)
                try:
                    with open(file_path, "r", encoding="utf-8") as f:
                        content = f.read()

                    tree = ast.parse(content)

                    signatures = []
                    for node in ast.walk(tree):
                        if isinstance(node, ast.FunctionDef):
                            signatures.append("Function: " + get_function_signature(node))
                        elif isinstance(node, ast.ClassDef):
                            signatures.append("Class: " + get_class_signature(node))

                    if signatures:
                        context.append(f"File: {file_path}\n" + "\n".join(signatures) + "\n")

                    file_count += 1
                    if file_count >= max_files:
                        break
                except Exception as e:
                    print(f"Error processing {file_path}: {e}")
        if file_count >= max_files:
            break
    return "\n".join(context)
