import pandas as pd

class DataLoader:
    def __init__(self, file_path, chunksize=10000):
        self.file_path = file_path
        self.chunksize = chunksize

    def load_data(self):
        """Load data in chunks to reduce memory usage."""
        reader = pd.read_csv(self.file_path, chunksize=self.chunksize)
        for chunk in reader:
            # Convert to numpy array for faster processing
            yield chunk.to_numpy()

    def get_data_shape(self):
        """Get the shape of the data without loading the entire file."""
        # Read only the first row to get the number of columns
        try:
            df = pd.read_csv(self.file_path, nrows=1)
            num_cols = df.shape[1]
            # Returning None for rows as it's not feasible to know without reading the whole file
            return (None, num_cols)
        except Exception as e:
            print(f"Error getting data shape: {e}")
            return (None, None)

