class DataLoader:
    def __init__(self, file_path):
        self.file_path = file_path

    def load_data(self):
        """Loads all data at once (less memory efficient)."""
        with open(self.file_path, 'r') as f:
            data = json.load(f)
        return data

    def stream_data(self):
        """Streams data using a generator (more memory efficient)."""
        with open(self.file_path, 'r') as f:
            decoder = json.JSONDecoder()
            buffer = ''
            for chunk in f:
                buffer += chunk
                while True:
                    try:
                        obj, idx = decoder.raw_decode(buffer)
                        yield obj
                        buffer = buffer[idx:].lstrip()
                    except json.JSONDecodeError:
                        break
            if buffer:
                try:
                    obj, idx = decoder.raw_decode(buffer)
                    yield obj
                except json.JSONDecodeError:
                    pass


