    "content": "import numpy as np

def quaternion_conjugate(q):
    return np.array([q[0], -q[1], -q[2], -q[3]])

def quaternion_multiply(q1, q2):
    w1, x1, y1, z1 = q1
    w2, x2, y2, z2 = q2
    w = w1 * w2 - x1 * x2 - y1 * y2 - z1 * z2
    x = w1 * x2 + x1 * w2 + y1 * z2 - z1 * y2
    y = w1 * y2 - x1 * z2 + y1 * w2 + z1 * x2
    z = w1 * z2 + x1 * y2 - y1 * x2 + z1 * w2
    return np.array([w, x, y, z])


def quaternion_to_rotation_matrix(q):
    q = normalize_quaternion(q)
    w, x, y, z = q
    R = np.array([
        [1 - 2*y**2 - 2*z**2, 2*x*y - 2*w*z, 2*x*z + 2*w*y],
        [2*x*y + 2*w*z, 1 - 2*x**2 - 2*z**2, 2*y*z - 2*w*x],
        [2*x*z - 2*w*y, 2*y*z + 2*w*x, 1 - 2*x**2 - 2*y**2]
    ])
    return R


def normalize_quaternion(q, epsilon=1e-12):
    norm = np.linalg.norm(q)
    if norm < epsilon:
        return np.array([1.0, 0.0, 0.0, 0.0])  # Return identity quaternion if norm is too small
    return q / norm


def quaternion_inverse(q):
    q_conj = quaternion_conjugate(q)
    return q_conj / np.sum(q * q)


def rotate_vector(v, q):
    q = normalize_quaternion(q)
    q_conj = quaternion_conjugate(q)
    q_v = np.array([0, v[0], v[1], v[2]])
    q_rotated = quaternion_multiply(quaternion_multiply(q, q_v), q_conj)
    return q_rotated[1:]
