        q = np.array([1, 2, 3, 4])
        expected = np.array([1, -2, -3, -4])
        np.testing.assert_array_equal(quaternion_ops.quaternion_conjugate(q), expected)

    def test_quaternion_multiply(self):
        q1 = np.array([1, 2, 3, 4])
        q2 = np.array([5, 6, 7, 8])
        expected = np.array([-60,  12,  30,  24])
        np.testing.assert_array_equal(quaternion_ops.quaternion_multiply(q1, q2), expected)

    def test_quaternion_to_rotation_matrix(self):
        q = np.array([1, 0, 0, 0])  # Identity quaternion
        expected = np.eye(3)
        np.testing.assert_array_almost_equal(quaternion_ops.quaternion_to_rotation_matrix(q), expected)

    def test_normalize_quaternion(self):
        q = np.array([1, 2, 3, 4])
        normalized_q = quaternion_ops.normalize_quaternion(q)
        np.testing.assert_almost_equal(np.linalg.norm(normalized_q), 1.0)

        # Test with a near-zero quaternion
        q_near_zero = np.array([1e-13, 0, 0, 0])
        normalized_q_near_zero = quaternion_ops.normalize_quaternion(q_near_zero)
        np.testing.assert_array_almost_equal(normalized_q_near_zero, np.array([1.0, 0.0, 0.0, 0.0]))

