    "content": "import os
import torch

class Trainer:
    def __init__(self, model, optimizer, data_loader, device, checkpoint_dir, checkpoint_interval=1000):
        self.model = model
        self.optimizer = optimizer
        self.data_loader = data_loader
        self.device = device
        self.checkpoint_dir = checkpoint_dir
        self.checkpoint_interval = checkpoint_interval
        os.makedirs(self.checkpoint_dir, exist_ok=True)
        self.step = 0

    def train_one_epoch(self):
        for batch_idx, (data, target) in enumerate(self.data_loader):
            data, target = data.to(self.device), target.to(self.device)
            self.optimizer.zero_grad()
            output = self.model(data)
            loss = torch.nn.functional.cross_entropy(output, target)
            loss.backward()
            self.optimizer.step()

            if self.step % 100 == 0:
                print(f"Step: {self.step}, Loss: {loss.item()}")

            if self.step % self.checkpoint_interval == 0:
                self.save_checkpoint()

            self.step += 1

    def train(self, epochs):
        for epoch in range(epochs):
            print(f"Epoch {epoch+1}/{epochs}")
            self.train_one_epoch()

    def save_checkpoint(self):
        checkpoint_path = os.path.join(self.checkpoint_dir, f'checkpoint_{self.step}.pth')
        torch.save({
            'step': self.step,
            'model_state_dict': self.model.state_dict(),
            'optimizer_state_dict': self.optimizer.state_dict(),
        }, checkpoint_path)
        print(f"Checkpoint saved to {checkpoint_path}")

    def load_checkpoint(self, checkpoint_path):
        checkpoint = torch.load(checkpoint_path)
        self.model.load_state_dict(checkpoint['model_state_dict'])
        self.optimizer.load_state_dict(checkpoint['optimizer_state_dict'])
        self.step = checkpoint['step']
        print(f"Checkpoint loaded from {checkpoint_path} at step {self.step}")"
  }
