import ast
import glob

class EvolutionSystem:
    def __init__(self, project_dir):
        self.project_dir = project_dir

    def get_project_context(self):
        """Collects relevant code context from the project.
        This version includes AST parsing for more precise class and function extraction.
        """
        context = []

        # Define a list of files to include in the context
        file_patterns = [
            'h2q_project/**/*.py',  # Include all Python files in the project directory
            'h2q_project/**/Trainer.py', # Specific files like Trainer classes
            'h2q_project/**/losses.py' # files with loss functions
        ]

        for pattern in file_patterns:
            for filepath in glob.glob(os.path.join(self.project_dir, pattern), recursive=True):
                try:
                    with open(filepath, 'r', encoding='utf-8') as f:
                        content = f.read()

                    # Use AST to parse the code and extract classes and function definitions
                    tree = ast.parse(content)
                    for node in ast.walk(tree):
                        if isinstance(node, (ast.ClassDef, ast.FunctionDef)):
                            # Extract name and signature (very basic)
                            if isinstance(node, ast.ClassDef):
                                signature = f"class {node.name}"
                            else:
                                signature = f"def {node.name}"

                            #Basic function and class extraction
                            context.append(f"File: {filepath}\n{signature}\n")

                except Exception as e:
                    print(f"Error reading or parsing {filepath}: {e}")

        return "\n".join(context)


if __name__ == '__main__':
