            'input': input_data,
            'output': output_data,
            'result': result
        }

        with open(self.log_file, 'a') as f:
            f.write(json.dumps(log_entry) + '\n')

    def load_logs(self):
        logs = []
        try:
            with open(self.log_file, 'r') as f:
                for line in f:
                    logs.append(json.loads(line))
        except FileNotFoundError:
            pass
