import os

class EvolutionSystem:
    def __init__(self, evo_state_path='evo_state.json'):
        self.evo_state_path = evo_state_path
        self.evo_state_cache = None  # Initialize cache

    def load_evo_state(self):
        if self.evo_state_cache is not None:
            return self.evo_state_cache  # Return cached state

        try:
            with open(self.evo_state_path, 'r') as f:
                self.evo_state_cache = json.load(f)  # Load into cache
                return self.evo_state_cache
        except FileNotFoundError:
            return {}

    def save_evo_state(self, evo_state):
        self.evo_state_cache = evo_state  # Update cache
        with open(self.evo_state_path, 'w') as f:
            json.dump(evo_state, f, indent=4)

    def get_stat(self, stat_name):
        evo_state = self.load_evo_state()
        return evo_state.get(stat_name, 0)

    def update_stat(self, stat_name, value):
        evo_state = self.load_evo_state()
        evo_state[stat_name] = value
        self.save_evo_state(evo_state)

    def reset_evo_state(self):
        self.evo_state_cache = {}
        try:
            os.remove(self.evo_state_path)
        except FileNotFoundError:
            pass

    def increment_stat(self, stat_name):
        current_value = self.get_stat(stat_name)
        self.update_stat(stat_name, current_value + 1)


if __name__ == '__main__':
