    "content": "import torch
import torch.nn as nn
import torch.optim as optim
from torch.utils.data import DataLoader

class Trainer:
    def __init__(self, model: nn.Module, train_dataloader: DataLoader, val_dataloader: DataLoader, optimizer_config: dict = None, device: str = "cpu"):
        self.model = model.to(device)
        self.train_dataloader = train_dataloader
        self.val_dataloader = val_dataloader
        self.device = device

        # Default optimizer configuration
        default_optimizer_config = {
            "lr": 0.001,
            "weight_decay": 0.0
        }

        # Update default config with user-provided config
        if optimizer_config:
            default_optimizer_config.update(optimizer_config)

        self.optimizer = optim.AdamW(self.model.parameters(), lr=default_optimizer_config["lr"], weight_decay=default_optimizer_config["weight_decay"])
        self.loss_fn = nn.CrossEntropyLoss()

    def train_epoch(self):
        self.model.train()
        total_loss = 0
        for batch in self.train_dataloader:
            inputs, labels = batch[0].to(self.device), batch[1].to(self.device)
            self.optimizer.zero_grad()
            outputs = self.model(inputs)
            loss = self.loss_fn(outputs, labels)
            loss.backward()
            self.optimizer.step()
            total_loss += loss.item()
        return total_loss / len(self.train_dataloader)

    def validate_epoch(self):
        self.model.eval()
        total_loss = 0
        with torch.no_grad():
            for batch in self.val_dataloader:
                inputs, labels = batch[0].to(self.device), batch[1].to(self.device)
                outputs = self.model(inputs)
                loss = self.loss_fn(outputs, labels)
                total_loss += loss.item()
        return total_loss / len(self.val_dataloader)

    def train(self, epochs: int):
        for epoch in range(epochs):
            train_loss = self.train_epoch()
            val_loss = self.validate_epoch()
            print(f"Epoch {epoch+1}, Train Loss: {train_loss:.4f}, Val Loss: {val_loss:.4f}")"
  }
