logging.basicConfig(level=logging.INFO, format='%(asctime)s - %(levelname)s - %(message)s')

class CustomError(Exception):
    """Base class for custom exceptions in the project."""
    def __init__(self, message, details=None):
        super().__init__(message)
        self.message = message
        self.details = details

    def __str__(self):
        return f"{self.__class__.__name__}: {self.message} (Details: {self.details})" if self.details else f"{self.__class__.__name__}: {self.message}"

def safe_operation(func, *args, **kwargs):
    """A decorator to handle exceptions and log errors."""
    try:
        return func(*args, **kwargs)
    except Exception as e:
        logging.error(f"Error in {func.__name__}: {e}", exc_info=True)
        raise CustomError(f"Operation failed in {func.__name__}", details=str(e)) from e


def log_info(message):
    logging.info(message)


def log_warning(message):
    logging.warning(message)
