import torch.nn as nn
import torch.optim as optim
from torch.optim.lr_scheduler import StepLR
from torch.utils.data import DataLoader

class Trainer:
    def __init__(self, model, train_dataset, val_dataset, config=None):
        self.model = model
        self.train_dataset = train_dataset
        self.val_dataset = val_dataset

        # Default configurations
        default_config = {
            'optimizer': {
                'name': 'Adam',
                'params': {'lr': 1e-3}
            },
            'lr_scheduler': {
                'name': 'StepLR',
                'params': {'step_size': 5, 'gamma': 0.1}
            },
            'loss_fn': 'CrossEntropyLoss',
            'batch_size': 32,
            'epochs': 10,
            'device': 'cuda' if torch.cuda.is_available() else 'cpu'
        }

        # Override with user-provided configurations
        self.config = default_config
        if config:
            self.config.update(config)

        # Initialize components based on configuration
        self.device = torch.device(self.config['device'])
        self.model.to(self.device)

        # Optimizer
        optimizer_name = self.config['optimizer']['name']
        optimizer_params = self.config['optimizer']['params']
        self.optimizer = getattr(optim, optimizer_name)(self.model.parameters(), **optimizer_params)

        # Learning Rate Scheduler
        lr_scheduler_name = self.config['lr_scheduler']['name']
        lr_scheduler_params = self.config['lr_scheduler']['params']
        self.lr_scheduler = getattr(optim.lr_scheduler, lr_scheduler_name)(self.optimizer, **lr_scheduler_params)

        # Loss Function
        loss_fn_name = self.config['loss_fn']
        self.loss_fn = getattr(nn, loss_fn_name)()

        # DataLoaders
        self.train_loader = DataLoader(self.train_dataset, batch_size=self.config['batch_size'], shuffle=True)
        self.val_loader = DataLoader(self.val_dataset, batch_size=self.config['batch_size'], shuffle=False)

    def train_epoch(self):
        self.model.train()
        running_loss = 0.0
        for i, (inputs, labels) in enumerate(self.train_loader):
            inputs, labels = inputs.to(self.device), labels.to(self.device)

            self.optimizer.zero_grad()

            outputs = self.model(inputs)
            loss = self.loss_fn(outputs, labels)
            loss.backward()
            self.optimizer.step()

            running_loss += loss.item()

        return running_loss / len(self.train_loader)

    def validate_epoch(self):
        self.model.eval()
        running_loss = 0.0
        with torch.no_grad():
            for i, (inputs, labels) in enumerate(self.val_loader):
                inputs, labels = inputs.to(self.device), labels.to(self.device)

                outputs = self.model(inputs)
                loss = self.loss_fn(outputs, labels)

                running_loss += loss.item()

        return running_loss / len(self.val_loader)

    def train(self):
        for epoch in range(self.config['epochs']):
            train_loss = self.train_epoch()
            val_loss = self.validate_epoch()
            self.lr_scheduler.step()

            print(f'Epoch {epoch+1}/{self.config["epochs"]}, Train Loss: {train_loss:.4f}, Val Loss: {val_loss:.4f}')"
