    "content": "import torch
import os
import psutil

class Trainer:
    def __init__(self, model, optimizer, criterion, device=None, batch_size=32):
        self.model = model
        self.optimizer = optimizer
        self.criterion = criterion
        self.device = device if device else self._get_default_device()
        self.batch_size = batch_size
        self.model.to(self.device)

    def _get_default_device(self):
        if torch.cuda.is_available():
            return torch.device('cuda')
        else:
            return torch.device('cpu')

    def _adjust_batch_size(self, data):
        """Adjusts batch size based on available memory to prevent OOM."""
        available_memory = psutil.virtual_memory().available / (1024 * 1024)  # Available memory in MB
        estimated_memory_usage = len(data) * self.get_tensor_size(data[0]) / (1024 * 1024) # Estimated memory usage in MB per batch.
        #Reduce batch size if estimated usage approaches available memory.
        while estimated_memory_usage * self.batch_size > available_memory * 0.75 and self.batch_size > 1:
            self.batch_size //= 2
            print(f"Reducing batch size to {self.batch_size} to prevent OOM.")

    def get_tensor_size(self, tensor): #Simplified Tensor size calculation
         element_size = tensor.element_size()
         num_elements = tensor.nelement()
         return element_size * num_elements


    def train_epoch(self, data_loader):
        self.model.train()
        running_loss = 0.0
        for data, target in data_loader:
            self._adjust_batch_size(data)
            data, target = data[:self.batch_size].to(self.device), target[:self.batch_size].to(self.device)
            self.optimizer.zero_grad()
            output = self.model(data)
            loss = self.criterion(output, target)
            loss.backward()
            self.optimizer.step()
            running_loss += loss.item() * data.size(0)
        return running_loss / len(data_loader.dataset)

    def evaluate(self, data_loader):
        self.model.eval()
        running_loss = 0.0
        with torch.no_grad():
            for data, target in data_loader:
                data, target = data.to(self.device), target.to(self.device)
                output = self.model(data)
                loss = self.criterion(output, target)
                running_loss += loss.item() * data.size(0)
        return running_loss / len(data_loader.dataset)"
