import traceback


class EvolutionSystem:
    def __init__(self, code_generator, code_evaluator, code_improver):
        self.code_generator = code_generator
        self.code_evaluator = code_evaluator
        self.code_improver = code_improver
        self.failed_generation_attempts = {}

    def evolve(self, task_description):
        try:
            generated_code = self.code_generator.generate_code(task_description)
        except Exception as e:
            logging.error(f"Code generation failed: {e}")
            trace = traceback.format_exc()
            logging.error(f"Traceback: {trace}")

            # Log and analyze the failure
            failure_message = str(e) + "\n" + trace
            if failure_message not in self.failed_generation_attempts:
                self.failed_generation_attempts[failure_message] = 0
            self.failed_generation_attempts[failure_message] += 1

            logging.warning(f"This failure has occurred {self.failed_generation_attempts[failure_message]} times.")

            return None  # Or raise the exception, depending on desired behavior

        evaluation_result = self.code_evaluator.evaluate_code(generated_code, task_description)

        if evaluation_result["success"]:
            return generated_code
        else:
            improved_code = self.code_improver.improve_code(generated_code, evaluation_result["errors"], task_description)
            return improved_code"
