import torch.nn as nn
import torch.optim as optim
from torch.utils.data import DataLoader

# Re-use existing project abstractions where appropriate
# Example: Assuming there's already a defined 'Model' and 'Dataset'
# from h2q_project.models import Model
# from h2q_project.datasets import Dataset


class Trainer:
    def __init__(self, model, train_dataset, val_dataset, config):
        self.model = model
        self.train_dataset = train_dataset
        self.val_dataset = val_dataset
        self.config = config

        self.device = config.get('device', 'cuda' if torch.cuda.is_available() else 'cpu')
        self.model.to(self.device)

        self.train_dataloader = DataLoader(self.train_dataset, batch_size=config.get('batch_size', 32), shuffle=True)
        self.val_dataloader = DataLoader(self.val_dataset, batch_size=config.get('batch_size', 32), shuffle=False)

        self.optimizer = optim.Adam(self.model.parameters(), lr=config.get('learning_rate', 1e-3))
        self.criterion = nn.CrossEntropyLoss()  # Example loss function.  Make configurable if needed.
        self.epochs = config.get('epochs', 10)

    def train_step(self, batch):
        # Implement a single training step
        inputs, labels = batch  # Assuming dataset returns (inputs, labels)
        inputs = inputs.to(self.device)
        labels = labels.to(self.device)

        self.optimizer.zero_grad()
        outputs = self.model(inputs)
        loss = self.criterion(outputs, labels)
        loss.backward()
        self.optimizer.step()

        return loss.item()

    def val_step(self, batch):
        # Implement a single validation step
        inputs, labels = batch
        inputs = inputs.to(self.device)
        labels = labels.to(self.device)

        with torch.no_grad():
            outputs = self.model(inputs)
            loss = self.criterion(outputs, labels)

        return loss.item()

    def train_epoch(self, epoch):
        self.model.train()
        total_loss = 0
        for batch in self.train_dataloader:
            loss = self.train_step(batch)
            total_loss += loss
        print(f"Epoch {epoch+1}/{self.epochs}, Train Loss: {total_loss/len(self.train_dataloader):.4f}")

    def val_epoch(self, epoch):
        self.model.eval()
        total_loss = 0
        with torch.no_grad():
            for batch in self.val_dataloader:
                loss = self.val_step(batch)
                total_loss += loss
        print(f"Epoch {epoch+1}/{self.epochs}, Val Loss: {total_loss/len(self.val_dataloader):.4f}")

    def train(self):
        for epoch in range(self.epochs):
            self.train_epoch(epoch)
            self.val_epoch(epoch)

    def save_model(self, path):
        torch.save(self.model.state_dict(), path)


if __name__ == '__main__':
