def setup_logger(task_id):
    """Sets up a logger for a given task ID."""
    log_file_name = f'{datetime.datetime.now().strftime("%Y%m%d_%H%M%S")}_{task_id}.log'
    log_file_path = os.path.join(LOG_DIR, log_file_name)

    logger = logging.getLogger(task_id)
    logger.setLevel(logging.INFO)

    file_handler = logging.FileHandler(log_file_path)
    formatter = logging.Formatter('%(asctime)s - %(name)s - %(levelname)s - %(message)s')
    file_handler.setFormatter(formatter)

    logger.addHandler(file_handler)

    return logger


def log_event(logger, event_type, message):
    """Logs an event with a specific type and message."""
    if event_type == 'info':
        logger.info(message)
    elif event_type == 'warning':
        logger.warning(message)
    elif event_type == 'error':
        logger.error(message)
    else:
        logger.info(f'[{event_type}] {message}') # Default to info for unknown types
