    "content": "import numpy as np

class FractalGenerator:
    def __init__(self, max_iter=100, escape_radius=2.0):
        self.max_iter = max_iter
        self.escape_radius = escape_radius

    def mandelbrot(self, c, max_iter=None, escape_radius=None):
        if max_iter is None:
            max_iter = self.max_iter
        if escape_radius is None:
            escape_radius = self.escape_radius

        z = 0
        for n in range(max_iter):
            z = z*z + c
            if abs(z) > escape_radius:
                return n
        return max_iter

    def generate_fractal(self, x_start, x_end, y_start, y_end, width, height):
        image = np.zeros((height, width))
        x = np.linspace(x_start, x_end, width)
        y = np.linspace(y_start, y_end, height)

        for i in range(height):
            for j in range(width):
                c = complex(x[j], y[i])
                image[i, j] = self.mandelbrot(c)
        return image

    def generate_fractal_numpy(self, x_start, x_end, y_start, y_end, width, height):
        # Use numpy for faster calculation
        x, y = np.meshgrid(np.linspace(x_start, x_end, width), np.linspace(y_start, y_end, height))
        c = x + 1j * y
        n = np.zeros_like(c, dtype=int)
        z = np.zeros_like(c, dtype=complex)
        for i in range(self.max_iter):
            z = z * z + c
            mask = (abs(z) < self.escape_radius) & (n == 0)
            n[mask] = i + 1 # Assign iteration count only where it hasn't been assigned yet

        n[n == 0] = self.max_iter # Assign max_iter where it hasn't escaped
        return n


