class EvolutionSystem:
    def __init__(self):
        self.client = docker.from_env()

    def run_container(self, image_name, command=None, cpu_count=1, memory_limit='1g'):
        """Runs a Docker container with specified resource limits."""
        container = self.client.containers.run(
            image_name,
            command=command,
            detach=True,
            cpus=cpu_count,  # Limit to a specific number of CPUs
            mem_limit=memory_limit  # Limit memory usage
        )
        return container

    def stop_container(self, container):
        container.stop()

    def remove_container(self, container):
        container.remove()

    def get_container_logs(self, container):
        return container.logs().decode('utf-8')"
