        self.code_executor = code_executor
        self.logger = logger

    def evolve(self, task_description):
        try:
            # Code Generation
            generated_code = self.code_generator.generate_code(task_description)

            # Code Validation
            validation_result = self.code_validator.validate_code(generated_code)

            if validation_result["valid"]:
                # Code Execution
                execution_result = self.code_executor.execute_code(generated_code)
                if execution_result["success"]:
                    self.logger.log(f"Task '{task_description}' completed successfully.")
                    return execution_result
                else:
                    self.logger.log(f"Execution failed for task '{task_description}'. Error: {execution_result['error']}")
                    # Code Improvement
                    improved_code = self.code_improver.improve_code(generated_code, execution_result["error"])
                    # Re-validation after improvement
                    validation_result_after_improvement = self.code_validator.validate_code(improved_code)
                    if validation_result_after_improvement["valid"]:
                        execution_result_after_improvement = self.code_executor.execute_code(improved_code)
                        if execution_result_after_improvement["success"]:
                            self.logger.log(f"Task '{task_description}' completed successfully after improvement.")
                            return execution_result_after_improvement
                        else:
