import os

class SelfReflection:
    def __init__(self, memory_file='reflection_memory.json'):
        self.memory_file = memory_file
        self.memory = self.load_memory()

    def load_memory(self):
        if os.path.exists(self.memory_file):
            with open(self.memory_file, 'r') as f:
                return json.load(f)
        else:
            return {"successes": [], "failures": []}

    def save_memory(self):
        with open(self.memory_file, 'w') as f:
            json.dump(self.memory, f, indent=4)

    def record_success(self, task_description, result):
        self.memory["successes"].append({"task": task_description, "result": result})
        self.save_memory()

    def record_failure(self, task_description, error):
        self.memory["failures"].append({"task": task_description, "error": error})
        self.save_memory()

    def get_past_failures(self):
        return self.memory["failures"]

    def get_past_successes(self):
        return self.memory["successes"]

    def reflect(self, task_description):
        past_failures = self.get_past_failures()
        past_successes = self.get_past_successes()

        reflection_text = f"Reflecting on task: {task_description}\n"

        if past_failures:
            reflection_text += "\nPast Failures:\n"
            for failure in past_failures:
                reflection_text += f"- Task: {failure['task']}, Error: {failure['error']}\n"
        else:
            reflection_text += "\nNo past failures recorded.\n"

        if past_successes:
            reflection_text += "\nPast Successes:\n"
            for success in past_successes:
                reflection_text += f"- Task: {success['task']}, Result: {success['result']}\n"
        else:
            reflection_text += "\nNo past successes recorded.\n"

        return reflection_text

if __name__ == '__main__':
    # Example Usage:
    reflection = SelfReflection()

    # Simulate a failed task
    reflection.record_failure("Generate a complex math equation", "Failed to import necessary libraries.")

    # Simulate a successful task
    reflection.record_success("Write a simple 'Hello, world!' program", "Program executed successfully.")

    # Reflect on a new task
    new_task = "Develop a self-learning algorithm"
    reflection_output = reflection.reflect(new_task)
    print(reflection_output)
