import os
import logging

# Configure logging
logging.basicConfig(level=logging.INFO, format='%(asctime)s - %(levelname)s - %(message)s')

class EvolutionSystem:
    def __init__(self, project_path, test_command):
        self.project_path = project_path
        self.test_command = test_command

    def apply_patch(self, file_path, content):
        full_path = os.path.join(self.project_path, file_path)
        try:
            with open(full_path, 'w') as f:
                f.write(content)
            logging.info(f"Successfully applied patch to {file_path}")
        except Exception as e:
            logging.error(f"Failed to apply patch to {file_path}: {e}")
            return False
        return True

    def verify_patch(self):
        try:
            process = subprocess.Popen(self.test_command, shell=True, cwd=self.project_path, stdout=subprocess.PIPE, stderr=subprocess.PIPE)
            stdout, stderr = process.communicate()
            return_code = process.returncode

            logging.info(f"Test command output:\n{stdout.decode()}")
            if stderr:
                logging.error(f"Test command error output:\n{stderr.decode()}")

            if return_code == 0:
                logging.info("Patch verification successful.")
                return True
            else:
                logging.warning("Patch verification failed.")
                return False
        except Exception as e:
            logging.error(f"Patch verification failed: {e}")
            return False

    def evolve(self, file_path, content):
        try:
            if not self.apply_patch(file_path, content):
                logging.error("Failed to apply patch. Aborting evolution.")
                return False

            if not self.verify_patch():
                logging.warning("Patch verification failed. Evolution unsuccessful.")
                return False

            logging.info("Evolution successful.")
            return True

        except Exception as e:
            logging.error(f"Evolution process failed: {e}")
