    "content": "import psutil
import time
import json

class PerformanceMonitor:
    def __init__(self, interval=1):
        self.interval = interval
        self.data = []

    def record(self):
        cpu_usage = psutil.cpu_percent(interval=self.interval)
        memory_usage = psutil.virtual_memory().percent
        timestamp = time.time()
        self.data.append({
            "timestamp": timestamp,
            "cpu_usage": cpu_usage,
            "memory_usage": memory_usage
        })

    def get_data(self):
        return self.data

    def save_to_json(self, filename="performance_data.json"):
        with open(filename, "w") as f:
            json.dump(self.data, f, indent=4)

if __name__ == '__main__':
    monitor = PerformanceMonitor(interval=0.5)
    print("Monitoring performance. Press Ctrl+C to stop.")
    try:
        while True:
            monitor.record()
            time.sleep(monitor.interval)
    except KeyboardInterrupt:
        print("\nMonitoring stopped.")
        data = monitor.get_data()
        print(f"Collected {len(data)} data points.")
        monitor.save_to_json()
        print("Data saved to performance_data.json")"
